use C4::URL::Checker;

        my $checker = C4::URL::Checker->new( );
        $checker->{ host_default } = 'http://mylib.kohalibrary.com';
        my $checked_urls = $checker->check_biblio( 123 );
        foreach my $url ( @$checked_urls ) {
            print "url:        ", $url->{ url       A }, "\n",
                  "is_success: ", $url->{ is_success }, "\n",
                  "status:     ", $url->{ status     }, "\n";
        }

